div.script:
    function myFunction() {
      var h = document.getElementById("height").value
      var w = document.getElementById("width").value
      var l = document.getElementById("length").value
      var calc = (h*l*w)/5000
      document.getElementById("dimensional_weight").value = (h*l*w)/5000
      var weight = document.getElementById("weight").value
      var no_box = document.getElementById("no_box").value
      if (calc >= weight) {
          document.getElementById("chargeable_weight").value = calc;
          document.getElementById("total_weight").value = calc * no_box;
        } else {
          document.getElementById("chargeable_weight").value = weight;
          document.getElementById("total_weight").value = weight * no_box;
        }
    }

