 function myFunction5() {
      var no_box = document.getElementById("no_box").value
      var no_box1 = document.getElementById("no_box1").value
      var no_box2 = document.getElementById("no_box2").value
      var no_box3 = document.getElementById("no_box3").value
      var no_box4 = document.getElementById("no_box4").value
      document.getElementById("no_of_pieces").value = Number(no_box) + Number(no_box1) + Number(no_box2) + Number(no_box3) + Number(no_box4);
    }
